Analytics.Script:
CREATE TEMPORARY TABLE DeviceACData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_AC");
CREATE TEMPORARY TABLE DeviceACSummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_AC_SUMMARY", schema "AC FLOAT, deviceType STRING -i, deviceId STRING -i, owner STRING -i, time LONG -i",primaryKeys "deviceType, deviceId, owner, time");
insert overwrite table DeviceACSummaryData select AC, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time as BIGINT)as time from DeviceACData group by AC, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time as BIGINT);
