Analytics.Script:
CREATE TEMPORARY TABLE DeviceData6hr USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES6hr");
CREATE TEMPORARY TABLE DeviceSummaryData6hr USING CarbonAnalytics OPTIONS (tableName
"DEVICE_SUMMARY6hr", schema "temperature DOUBLE, motion DOUBLE, humidity DOUBLE, light DOUBLE, deviceType STRING -i, deviceId STRING -i, owner STRING
-i, time LONG -i",primaryKeys "deviceType, deviceId, owner, time");
insert overwrite table DeviceSummaryData6hr select temperature, motion, humidity, light, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time as BIGINT)as time from DeviceData6hr group by temperature, motion, humidity, light, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time as BIGINT);
