Analytics.Script:
CREATE TEMPORARY TABLE Devices3Data USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_IOT_DEVICES_s3");
CREATE TEMPORARY TABLE Devices3SummaryData USING CarbonAnalytics OPTIONS (tableName "DEVICE_s3_SUMMARY", schema "s3 FLOAT, deviceType STRING -i, deviceId STRING -i, owner STRING -i, time LONG -i",primaryKeys "deviceType, deviceId, owner, time");
insert overwrite table Devices3SummaryData select s3, meta_deviceType as deviceType, meta_deviceId as deviceId, meta_owner as owner, cast(meta_time as BIGINT)as time from Devices3Data group by s3, meta_deviceType, meta_deviceId, meta_owner, cast(meta_time as BIGINT);
